package com.microsoft.did.sdk

import com.microsoft.did.sdk.identifier.resolvers.Resolver
import com.microsoft.did.sdk.util.serializer.Serializer
import io.mockk.mockk
import org.assertj.core.api.Assertions.assertThat
import org.junit.Test

class DnsBindingServiceTest {

    private val resolver: Resolver = mockk()
    private val serializer = Serializer()
    private val dnsBindingService = DnsBindingService(mockk(relaxed = true), resolver, serializer)

    @Test
    fun `test deserialize config doc jwt`() {
        val docJwt = "eyJhbGciOiJFUzI1NksiLCJraWQiOiJkaWQ6aW9uOkVpRElqcmFxaTI2T0ptRzN6TTc0TGYwRzZjRWpQWXNDb1VLcDFXT1gzT0FFd2c_.eyJAY29udGV4dCI6Imh0dHBzOi8vaWRlbnRpdHkuZm91bmRhdGlvbi8ud2VsbC1rbm93bi9jb250ZXh0cy9kaWQtY29uZmlndXJhdGlvbi12MC4wLmpzb25sZCIsImxpbmtlZF9kaWRzIjpbeyJzdWIiOiJkaWQ6aW9uOkVpRElqcmFxaTI2T0ptRzN6TTc0TGYwRzZjRWpQWXNDb1VLcDFXT1gzT0FFd2c__.MXxIIJHPTHQzu8UIR-39SezOnSU1hMfzHxWkUAoKDhO5BIG5f2awuX7ooRmuBpevm4VBPqsVVSA9aVwjqQ9nmA"
        val domLinkCreds = dnsBindingService.deserializeConfigDocument(docJwt)
        val expectedDid = "did:ion:EiDIjraqi26OJmG3zM74Lf0G6cEjPYsCoUKp1WOX3OAEwg?-ion-initial-state=eyJkZWx0YV9oYXNoIjoiRWlEUElfSEJMTUhXdjZOV2I5Zmg0U0EzdTlwdURaWlRKTzZPeEJsTWktc3BLUSIsInJlY292ZXJ5X2NvbW1pdG1lbnQiOiJFaUJQWTU0aGxIZDhvdGZPS1plMmw0Ymh3bVg5NGdtTnMwQ3V5bnl5aVd5YnBRIn0."
        assertThat(domLinkCreds.size).isGreaterThan(0)
        assertThat(domLinkCreds.first().iss).isEqualTo(expectedDid)
        assertThat(domLinkCreds.first().sub).isEqualTo(expectedDid)
        assertThat(domLinkCreds.first().vc.issuer).isEqualTo(expectedDid)
        assertThat(domLinkCreds.first().vc.credentialSubject.id).isEqualTo(expectedDid)
    }
}